              shiny::tabsetPanel(
                id = 'TBATS_TabPanel',
                selected = 'Data',

                # Step 1: Data Parameters ----
                shiny::tabPanel(
                  title = "Data",
                  icon = shiny::icon('code'),
                  Quantico:::BlankRow(12L),
                  shiny::fluidRow(
                    shiny::column(
                      6L,
                      shinydashboard::box(
