          Quantico::BlankRow(12L),
          shiny::fluidRow(shiny::column(12L, Quantico:::PickerInput(InputID = paste0('FilterValue_', PlotNumber, '_1_2'), Label = "Value 2", Choices = NULL, SelectedDefault = NULL, Multiple = TRUE, MaxVars = 100)))
        )
